var address = "address=" + street + "," + city + "," + state + "," + zip + "&key='AIzaSyDAZKCqqwuMsUF-bpxO_y_3WZT1RLoYq7I";


var ws = new HTTPAdaptor("https://maps.googleapis.com/maps/api/geocode/json?");
